import { Component, OnInit } from '@angular/core';
import { FormBuilder, FormGroup, Validators } from '@angular/forms';
import { Router } from '@angular/router';
import { LoginService } from 'src/app/services/login.service';
import { LoginResponseDTO } from 'src/app/interfaces/login-response-dto';

@Component({
  selector: 'app-login',
  templateUrl: './login.component.html',
  styleUrls: ['./login.component.scss']
})
export class LoginComponent implements OnInit {

  loginForm: FormGroup = this.formBuilder.group({
    email: ['', [Validators.required]],
    senha: ['', [Validators.required]]
  });

  constructor(
    public formBuilder: FormBuilder,
    private router: Router,
    private loginService: LoginService // Injeção do serviço LoginService
  ) {}

  ngOnInit(): void {}

  loginUser() {
    if (this.loginForm.valid) {
      const { email, senha } = this.loginForm.value;
      this.loginService.login(email, senha).subscribe(
        (response: LoginResponseDTO) => {
          localStorage.setItem('token', response.token); // Guarda o token no local storage
          this.router.navigate(['/dashboard']); // Redireciona para a página de dashboard ou a página que você preferir
        },
        error => {
          alert("Erro ao realizar login"); // Mostra uma mensagem de erro se a autenticação falhar
        }
      );
    }
  }
}
<div class="container-login">
  <div class="container-login-central">
    <div class="container-header">
      <div class="titulo-login">
        Bem vindo
      </div>
      <div class="info-login">
        Forneça suas credenciais
      </div>
    </div>

    <div class="container-form">
      <form class="form-inputs" [formGroup]="loginForm" (submit)="loginUser()">
        <div class="group-input">
          <div class="text-info">
            Identificação
          </div>
          <input class="input-form" type="text" name="email" id="email" placeholder="Identificação"
                 formControlName="email" required>

          <label *ngIf="loginForm.get('email')?.errors?.['required'] &&  loginForm.get('email')?.touched"
                 class="error-input">
            Campo Obrigatório!
          </label>
        </div>

        <div class="group-input">
          <div class="text-info">
            Senha
          </div>
          <input class="input-form" type="password" name="senha" id="senha" placeholder="Senha"
                 formControlName="senha" required>

          <label *ngIf="loginForm.get('senha')?.errors?.['required'] &&  loginForm.get('senha')?.touched"
                 class="error-input">
            Campo Obrigatório!
          </label>
        </div>

        <div class="group-input">
          <input type="submit" class="btn-login" value="Login" [ngClass]="{'disabled' : !loginForm.valid}"
                 [disabled]="!loginForm.valid">
        </div>
      </form>
    </div>
  </div>
</div>
